/**
* Parent class that can handle input of data to wp_usermeta
*/
class extraField
{
	# name of the new user attribute in wp_usermeta
	protected $fieldNameInDB;
	
	# constructor
	public function __construct($fieldNameInDB)
	{
		$this->fieldNameInDB = $fieldNameInDB;
		
		add_action( 'personal_options_update', array($this, 'saveFieldsInDB'));
		add_action( 'edit_user_profile_update', array($this, 'saveFieldsInDB'));
	}
	
	# upadtes wp_usermeta
	public function saveFieldsInDB( $user_id )
	 {
		if(empty($_POST[$this->fieldNameInDB])) return;
		
 		update_user_meta( $user_id, $this->fieldNameInDB, $_POST[$this->fieldNameInDB]);
	 }
}

/**
* This class extends extraField and creates a Notes field that can be edited by admin and cannot be seen by users
*/
class ExtraFieldNotes extends ExtraField
{
    # name of the new user attribute in the registration form
	private $labelName;
	
	# constructor
	public function __construct($fieldNameInDB, $labelName)
	{
		parent::__construct($fieldNameInDB);
		$this->labelName = $labelName;
		#add_action( 'show_user_profile', array($this, 'addExtraLoginFields'));
		add_action( 'edit_user_profile', array($this, 'addExtraLoginFields'));
	}
	 
	# adds html to user registration form
	function addExtraLoginFields( $user ) 
	{?>
 	<table class="form-table">
 		<tr class = "custom_user_field_row_notes">
    		<th><label for="<?php $this->fieldNameInDB ?>"><?php echo $this->labelName ?></label></th>
    		<td>
				<textarea type="text" id="<?php echo $this->fieldNameInDB ?>" name="<?php echo $this->fieldNameInDB ?>" style="height:200px; width:500px"><?php echo  esc_attr( get_the_author_meta( $this->fieldNameInDB, $user->ID ));?></textarea>
   	    	</td>
    	</tr>
 	</table>
	<?php }
}

/**
*  This class extends ExtraField and takes care of the html layout of the field in the user profile
