/**
* This class sets up a radio button with two choices in a user profile form
*/
class ExtraFieldBiRadioProfile extends ExtraField
{
	# name of the new user attribute in the registration form
	private $labelName;
	#array of answer choices
	private $choices;
	
	# constructor
	public function __construct($fieldNameInDB, $labelName, $firstChoice, $secondChoice)
	{
		parent::__construct($fieldNameInDB);
		$this->labelName = $labelName;
		$this->choices = array(ucfirst(strtolower($firstChoice)), ucfirst(strtolower($secondChoice)));
	
		add_action('show_user_profile',array($this, 'addExtraLoginFields'));
		add_action( 'edit_user_profile', array($this, 'addExtraLoginFields'));
	}
	 
	# adds html to user registration form
	function addExtraLoginFields( $user ) 
	{?>
 	<table class="form-table">
 		<p class ="custom_user_field_row">
    		<label for="<?php $this->fieldNameInDB ?>">
			 	<b> <?php echo $this->labelName ?> </b>
    			<br></br>   	    	
			   <label><input type="radio" name="<?php echo $this->fieldNameInDB ?>" 
			   <?php if (strcasecmp(get_the_author_meta( $this->fieldNameInDB, $user->ID ),$this->choices[0]) == 0 ) { ?>checked="checked"<?php }?> value="<?php echo $this->choices[0] ?>">
			   <?php echo $this->choices[0] ?><br /></label>
			   
               <label><input type="radio" name="<?php echo $this->fieldNameInDB ?>"
			   <?php if (strcasecmp(get_the_author_meta($this->fieldNameInDB, $user->ID ), $this->choices[1]) == 0 ) { ?>checked="checked"<?php }?> value="<?php echo $this->choices[1] ?>">
			   <?php echo $this->choices[1] ?><br /></label>
			</label>
    	</p>
 	</table>
	<?php }
}

/**
* This class extends ExtraField and takes care of the html layout of the field in the new user registration form as well as handles
* data posting to wp_usermeta
*/
class ExtraFieldRegistration extends ExtraField
{
	# name of the new user attribute in the registration form
	private $labelName;
	
	# constructor
	public function __construct($fieldNameInDB, $labelName)
	{
		parent::__construct($fieldNameInDB);
		$this->labelName = $labelName;
	
		add_action('register_form',array($this, 'addExtraLoginFields'));
		add_action('user_register', array($this, 'saveFieldsInDB'));
	}
	 
	# adds html to user registration form
	function addExtraLoginFields( $user ) 
	{?>
 	<table class="form-table">
 		<p>
    		<label for="<?php $this->fieldNameInDB ?>">
			 	<?php echo $this->labelName ?>
    			<br></br>
    			<input type="text" id="<?php echo $this->fieldNameInDB ?>" name="<?php echo $this->fieldNameInDB ?>" size="20" value="<?php echo esc_attr( get_the_author_meta( $this->fieldNameInDB, $user->ID )); ?>">
   	    	</label>
    	</p>
 	</table>
	<?php }
}

/**
* This class sets up a radio button with two choices and handles data uploading to user_meta from a new user registration form
*/
class ExtraFieldBiRadioRegistration extends ExtraField
{
	# name of the new user attribute in the registration form
	private $labelName;
	#array of answer choices
	private $choices;
	
	# constructor
	public function __construct($fieldNameInDB, $labelName, $firstChoice, $secondChoice)
	{
		parent::__construct($fieldNameInDB);
		$this->labelName = $labelName;
		$this->choices = array(ucfirst(strtolower($firstChoice)), ucfirst(strtolower($secondChoice)));
	
		add_action('register_form',array($this, 'addExtraLoginFields'));
		add_action('user_register', array($this, 'saveFieldsInDB'));
	}
	 
	# adds html to user registration form
	function addExtraLoginFields( $user ) 
	{?>
 	<table class="form-table">
 		<p>
    		<label for="<?php $this->fieldNameInDB ?>">
			 	<b> <?php echo $this->labelName ?> </b>
    			<br></br>   	    	
			   <label><input type="radio"  name="<?php echo $this->fieldNameInDB ?>"  <?php if ($this->fieldNameInDB == $this->choices[0] ) { ?>checked="checked"<?php }?> value="<?php echo $this->choices[0] ?>">
			   <?php echo $this->choices[0] ?><br /></label>
               <label><input type="radio"  name="<?php echo $this->fieldNameInDB ?>"  <?php if ($this->fieldNameInDB == $this->choices[1] ) { ?>checked="checked"<?php }?> value="<?php echo $this->choices[1] ?>">
			   <?php echo $this->choices[1] ?><br /></label>
			</label>
			<br></br>  
    	</p>
 	</table>
	<?php }
}



